mapper.select:
SELECT EMPLOYEE.EMPNO, EMPLOYEE.EMPNM, EMPLOYEE.BIRTHDATE, EMPLOYEE.TELEPHONE, EMPLOYEE.ADDRESS FROM EMPLOYEE WHERE 1=1
AND EMPLOYEE.EMPNO LIKE #{searchKeyword}
AND EMPLOYEE.EMPNM LIKE #{searchKeyword}
ORDER BY EMPLOYEE.EMPNO DESC LIMIT #{recordCountPerPage} OFFSET #{firstIndex}


SELECT COUNT(*)
 FROM EMPLOYEE
 WHERE 1=1




        AND EMPLOYEE.EMPNO LIKE #{searchKeyword}


       AND EMPLOYEE.EMPNM LIKE #{searchKeyword}

mapper.update:

        UPDATE EMPLOYEE
        SET EMPLOYEE.EMPNM = #{empNm},
              	EMPLOYEE.BIRTHDATE = #{birthdate},
               	EMPLOYEE.TELEPHONE = #{telephone},
               	EMPLOYEE.ADDRESS = #{address}
	     WHERE EMPLOYEE.EMPNO = #{empNo}
